sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var resp={
		"status":"0000"
	};
	var mobilePhone=request.queryParams.mobilePhone+"";
	var smsCode=request.queryParams.smsCode+"";
	var areaCode=request.queryParams.areaCode+"";

	var gr=new GlideRecord("x_envision_itam_sms_code");
	gr.mobile_phone=mobilePhone;
	gr.sms_code=smsCode;
	var now=new GlideDateTime();
	gr.send_time=new GlideDateTime();
	now.addSeconds(5*60);
	gr.invalid_time=now;
	gr.error_code=request.queryParams.errorCode+"";
	gr.error_msg=request.queryParams.errorMsg+"";
	gr.request_id=request.queryParams.requestId+"";
	gr.area_code=areaCode.indexOf("+")>=0?areaCode:"+"+areaCode;

	if(gr.error_code!="OK"){
		gr.invalid_time=gr.send_time;
		gr.is_invalid=true;
		resp.status="9999";
		resp.msg="短信发送失败";
	}
	gr.insert();

	return resp;

})(request, response);
